$( document ).ready( function () {
      $( '.services' ).slick( {
            infinite: true,
            slidesToShow: 3,
            slidesToScroll: 3
      } );
} );

$( document ).ready( function () {
      $( '.artists' ).slick( {
            infinite: true,
            slidesToShow: 1,
            slidesToScroll: 3,
            autoplay: true,
            autoplaySpeed: 1000
      } );
} );

$( document ).ready( function () {
      $( '.brands' ).slick( {
            infinite: true,
            slidesToShow: 4,
            slidesToScroll: 1,
            autoplay: true,
            autoplaySpeed: 1000
      } );
} );